import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import './style.css';
import { setUserInfos } from '../../feature/user';
import { setToken } from '../../feature/user';

const Greeting = () => {
    const user = useSelector(state => state.user);
    const dispatch = useDispatch();

    const [display, setDisplay] = useState(false);
    const [editedUser, setEditedUser] = useState({
        username: user.username,
    });

    const handleChange = (event) => {
        const { id, value } = event.target;
        setEditedUser(prevState => ({
            ...prevState,
            [id]: value,
        }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        // Envoyer les nouvelles informations utilisateur au serveur
        fetch('/api/updateUser', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(editedUser),
        })
        .then(response => response.json())
        .then(data => {
            if (data.success) {
                // Mettre à jour les informations dans Redux
                dispatch(setUserInfos(editedUser));
                // Masquer le formulaire
                setDisplay(false);
            } else {
                // Gérer les erreurs côté serveur
                console.error('Update error, please try again');
            }
        })
        .catch(error => {
            console.error('Erreur :', error);
        });
    };

    const handleCancel = () => {
        setEditedUser({
            username: user.username,
            firstname: user.firstname,
            lastname: user.lastname,
        });
        setDisplay(false);
    };

    return (
        <div className='background'>
            <div className="header">
                <h1>Welcome back<br />{user.firstname} {user.lastname} !</h1>
                {!display && (
                    <button className="edit-button" onClick={() => setDisplay(true)}>
                        Edit Name
                    </button>
                )}
            </div>
            {display && (
                <div className='hidden-form'>
                    <h2 className='hidden-form-title'>Edit User Info</h2>
                    <form className="form" onSubmit={handleSubmit}>
                        <div className="edit-input">
                            <label htmlFor="username">User name:</label>
                            <input type="text" id="username" value={editedUser.username} onChange={handleChange} />
                        </div>
                        <div className="edit-input">
                            <label htmlFor="firstname">First name:</label>
                            <input type="text" id="firstname" value={user.firstname} readOnly/>
                        </div>
                        <div className="edit-input">
                            <label htmlFor="lastname">Last name:</label>
                            <input type="text" id="lastname" value={user.lastname}  readOnly />
                        </div>
                        <div className="buttons">
                            <button type="submit" className="edit-username-button" onChange={handleChange}>Save</button>
                            <button type="button" className="edit-username-button" onClick={handleCancel}>Cancel</button>
                        </div>
                    </form>
                </div>
            )}
        </div>
    );
};

export default Greeting;
