import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import './style.css';
import { setUserInfos, setUserName } from '../../feature/user';
import { setToken } from '../../feature/user';

const Greeting = () => {
    const user = useSelector(state => state.user);
    const dispatch = useDispatch();
    const [display, setDisplay] = useState(false);
    const [editedUser, setEditedUser] = useState({ username: user.username });
    const [error, setError] = useState('');

    const handleChange = (event) => {
        const { id, value } = event.target;
        setEditedUser(prevState => ({
            ...prevState,
            [id]: value,
        }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        fetch('http://localhost:3001/api/v1/user/profile', {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(editedUser),
        })
        .then(response => response.json())
        .then(data => {
            if (data.success) {
                // Mettre à jour les informations dans Redux
                dispatch(setUserName(editedUser.username));
                // Masquer le formulaire
                setDisplay(false);
                setError('');
            } else {
                // Gérer les erreurs côté serveur
                setError('Update error, please try again');
            }
        })
        .catch(error => {
            console.error('Erreur :', error);
            setError('Erreur lors de la mise à jour, veuillez réessayer.');
        });
    };

    const handleCancel = () => {
        setDisplay(false);
    };

    return (
        <div className='background'>
            <div className="header">
                <h1>Welcome back<br />{user.firstname} {user.lastname} !</h1>
                {!display && (
                    <button className="edit-button" onClick={() => setDisplay(true)}>
                        Edit Name
                    </button>
                )}
            </div>
            {display && (
                <div className='hidden-form'>
                    <h2 className='hidden-form-title'>Edit User Info</h2>
                    <form className="form" onSubmit={handleSubmit}>
                        <div className="edit-input">
                            <label htmlFor="username">User name:</label>
                            <input type="text" id="username" value={editedUser.username} onChange={handleChange} />
                        </div>
                        <div className="edit-input">
                            <label htmlFor="firstname">First name:</label>
                            <input type="text" id="firstname" value={user.firstname} readOnly/>
                        </div>
                        <div className="edit-input">
                            <label htmlFor="lastname">Last name:</label>
                            <input type="text" id="lastname" value={user.lastname} readOnly />
                        </div>
                        {error && <p className="error-message">{error}</p>}
                        <div className="buttons">
                            <button type="submit" className="edit-username-button">Save</button>
                            <button type="button" className="edit-username-button" onClick={handleCancel}>Cancel</button>
                        </div>
                    </form>
                </div>
            )}
        </div>
    );
};

export default Greeting;
