import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import './style.css';
import { setUserInfos } from '../../feature/user';

const Greeting = () => {
    const user = useSelector(state => state.user);
    const dispatch = useDispatch();

    const [display, setDisplay] = useState(false);
    const [editedUser, setEditedUser] = useState({
        username: user.username,
        firstname: user.firstname,
        lastname: user.lastname,
    });

    const handleChange = (event) => {
        const { id, value } = event.target;
        setEditedUser(prevState => ({
            ...prevState,
            [id]: value,
        }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        // Envoyer les nouvelles informations utilisateur au serveur (fetch)
        // Si la requête est réussie, mettre à jour les informations dans Redux
        // dispatch(setUserInfos(editedUser));
        // Réinitialiser les champs du formulaire et masquer le formulaire
        setEditedUser({
            username: user.username,
            firstname: user.firstname,
            lastname: user.lastname,
        });
        setDisplay(false);
    };

    return (
        <div className='background'>
            <div className="header">
                <h1>Welcome back<br />{user.firstname} {user.lastname}!</h1>
                <button className="edit-button" onClick={() => setDisplay(!display)}>
                    {display ? "Cancel" : "Edit Name"}
                </button>
            </div>
            {display && (
                <div>
                    <h2>Edit user info</h2>
                    <form className="form" onSubmit={handleSubmit}>
                        <div className="edit-input">
                            <label htmlFor="username">User name:</label>
                            <input type="text" id="username" value={editedUser.username} onChange={handleChange} />
                        </div>
                        <div className="edit-input">
                            <label htmlFor="firstname">First name:</label>
                            <input type="text" id="firstname" value={editedUser.firstname} onChange={handleChange} />
                        </div>
                        <div className="edit-input">
                            <label htmlFor="lastname">Last name:</label>
                            <input type="text" id="lastname" value={editedUser.lastname} onChange={handleChange} />
                        </div>
                        <div className="buttons">
                            <button type="submit" className="edit-username-button">Save</button>
                        </div>
                    </form>
                </div>
            )}
        </div>
    );
};

export default Greeting;
