import React from 'react';
import Logo from './logo.png';
import Iconuser from './circle-user-solid.svg';
import Iconlogout from './arrow-right-from-bracket-solid.svg';
import './style.css';
import { useSelector } from 'react-redux';

const Header = () => {
    const user = useSelector((state) => state.user);

    return (
        <div className='main-nav'>
            <a className="main-nav-logo" href="/">
                <img src={Logo} alt="Argent Bank Logo" />
            </a>
            <div>
                {user.token ? (
                    <>
                        <a className="main-nav-item" href="/user">
                            <img src={Iconuser} className='usericon' alt="user icon" />
                            {user.firstname}
                        </a>
                        <a className="main-nav-item" href="/signout">
                            <img src={Iconlogout} className='usericon' alt="logout icon" />
                            Sign out
                        </a>
                    </>
                ) : (
                    <a className="main-nav-item" href="/signin">
                        <img src={Iconuser} alt="user icon" className='usericon' />
                        Sign In
                    </a>
                )}
            </div>
        </div>
    );
};

export default Header;
