                    </div>
                    <div className="buttons">
                        <button className="edit-username-button" onClick={setUserInfos}>Save</button>
                        <button className="edit-username-button" onClick={() => setDisplay(!display)}>Cancel</button>
                    </div>
                </form>
            </div>
        </div>

    );
};

export default Greeting;
