import Icon from './circle-user-solid.svg';
import './style.css';
import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { setToken, setUserAuth } from '../../feature/user';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [rememberMe, setRememberMe] = useState(false);
    const [error, setError] = useState(false); // Change to false to initially hide error

    const fetchData = () => ({
        email: email.toString(),
        password: password.toString(),
    });

    const resetForm = () => {
        setEmail('');
        setPassword('');
        setRememberMe(false);
    };

    const dispatch = useDispatch();
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const response = await fetch("http://localhost:3001/api/v1/user/login", {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                    body: JSON.stringify(fetchData()),
                });

                if (response.ok) {
                    const data = await response.json();
                    const token = data.body.token;
                    dispatch(setToken({ token, rememberMe }));
                    dispatch(setUserAuth({ email, password }));
                    navigate('/user');
                    resetForm();
                } else {
                    setError(true); // Set error to true if response is not ok
                }
            } catch (error) {
                console.error("Error fetching data:", error);
                setError(true); // Set error to true in case of any errors
            }
        };

        fetchUserData();
    }, []); // Empty dependency array to execute only once on mount

    return (
        <section className="main-login">
            <div className="sign-in-content">
                <img src={Icon} alt="user icon" className='usericon'/>
                <h1>Sign In</h1>
                <form onSubmit={handleSubmit}>
                    <div className="input-wrapper">
                        <label htmlFor="username">Username</label>
                        <input id='username' type='text' value={email} onChange={(event) => setEmail(event.target.value)} />
                    </div>
                    <div className="input-wrapper">
                        <label htmlFor="password">Password</label>
                        <input id='password' type='text' value={password} onChange={(event) => setPassword(event.target.value)} />
                    </div>
                    <div className="input-remember">                       
                       <input id='remember-me' type='checkbox' checked={rememberMe} onChange={(event) => setRememberMe(event.target.checked)} />
                       <label htmlFor="remember-me">Remember me</label>
                    </div>
                    <button type="submit" className="sign-in-button">Sign In</button>
                    {error && <p>Une erreur est survenue</p>}
                </form>
            </div>
        </section>
    );
};

export default Login;
