import Icon from './circle-user-solid.svg';
import './style.css';
import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { setToken, setUserAuth } from '../../feature/login';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [rememberMe, setRememberMe] = useState(false);
    const [error, setError] = useState(false);

    const dispatch = useDispatch();
    const navigate = useNavigate();

    const resetForm = () => {
        setEmail('');
        setPassword('');
        setRememberMe(false);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await fetch("http://localhost:3001/api/v1/user/login", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ email, password }),
            });
            if (response.ok) {
                const data = await response.json();
                const token = data.body.token;
                dispatch(setToken({ token, rememberMe }));
                dispatch(setUserAuth({ email, password }));
                navigate('/user');
                resetForm(); // Reset form after successful login
            } else {
                setError(true);
            }
        } catch (error) {
            console.error("Error fetching data:", error);
            setError(true);
        }
    };

    return (
        <section className="main-login">
            <div className="sign-in-content">
                <img src={Icon} alt="user icon" className='usericon'/>
                <h1>Sign In</h1>
                <form onSubmit={handleSubmit}>
                    <div className="input-wrapper">
                        <label htmlFor="form-username">Username</label>
                        <input id='form-username' type='email' value={email} onChange={(event) => setEmail(event.target.value)} />
                    </div>
                    <div className="input-wrapper">
                        <label htmlFor="password">Password</label>
                        <input id='password' type='password' value={password} onChange={(event) => setPassword(event.target.value)} />
                    </div>
                    <div className="input-remember">                       
                        <input id='remember-me' type='checkbox' checked={rememberMe} onChange={(event) => setRememberMe(event.target.checked)} />
                        <label htmlFor="remember-me">Remember me</label>
                    </div>
                    <button type="submit" className="sign-in-button">Sign In</button>
                    {error && <p>Une erreur est survenue</p>}
                </form>
            </div>
        </section>
    );
};

export default Login;
