import React from 'react';
import {useSelector} from 'react-redux';
import './style.css'

const Greeting = () => {

    const user = useSelector(state => state.user);

    // TODO : Faire le formulaire, récupérer le token et envoyer le fetch pour modifier les infos
    // Si OK, on modifie dans redux les infos nom / prénom

    return (
        <div className='background'>
            <div className="header">
            <h1>Welcome back<br />{user.firstname} {user.lastname}!</h1>
            <button className="edit-button">Edit Name</button>
        </div>
        <div>
            <h2>Edit user info</h2>
                <form>
                    <div className="edit-input">
                        <label htmlFor="username">User name:</label>
                        <input type="text" id="username" defaultValue={userData.username} onChange={(event) => setUserName(event.target.value)} />
                    </div>
                    <div className="edit-input">
                        <label htmlFor="firstname">First name:</label>
                        <input type="text" id="firstname" defaultValue={userData.firstname} disabled={true} />
                    </div>
                    <div className="edit-input">
                        <label htmlFor="lastname">Last name:</label>
                        <input type="text" id="lastname" defaultValue={userData.lastname} disabled={true} />
                    </div>
                    <div className="buttons">
                        <button className="edit-username-button" onClick={handleSubmitUsername}>Save</button>
                        <button className="edit-username-button" onClick={() => setDisplay(!display)}>Cancel</button>
                    </div>
                    {errorMessage && <p className="error-message">{errorMessage}</p>}
                </form>
            </div>
        </div>
    );
};

export default Greeting;
