import Icon from './circle-user-solid.svg';
import './style.css';
import React, { useState } from 'react';

const Login = () => {

    // TODO : Faire le fetch pour récupérer le token à la connexion
    // TODO : Sauvbegarder le token dans redux avec dispatch
    //fetch('http://locahost:3001/api/v1/user/profile', {
          //  method: 'GET',
            //headers: {...}

        //})
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [rememberMe, setRememberMe] = useState(false);


    const Dispatch = useDispatch();
    const Navigate = useNavigate(); 

    const response = fetch("http://localhost:3001/api/v1/user/login", {
        method: "POST",
        headers: {
            "Content-Type": "application/json",
        },
        body: JSON.stringify({email, password}),
        });
        if (response.ok) {
            const data = response.json();    
            const token = data.body.token;
            dispatch((token));
            sessionStorage.setItem("token", token);
            if (rememberMe) {
                localStorage.setItem("token", token);
            }
            navigate('/user');
        } else {
            const error = "your email or password is invalid"
                dispatch((error));
        }   

    return (
        <section className="main-login">
            <div className="sign-in-content">
                <img src={Icon} alt="user icon" className='usericon'/>
                <h1>Sign In</h1>
                <form>
                    <div className="input-wrapper">
                        <label for="username">Username</label>
                        <input id='username' type='text' value={email} onChange={(event) => setEmail(event.target.value)} />
                    </div>
                    <div className="input-wrapper">
                        <label for="password">Password</label>
                        <input id='password' type='password' value={password} onChange={(event) => setPassword(event.target.value)} />
                    </div>
                    <div className="input-remember">
                        <label for="remember-me">Remember me</label>
                        <input id='remember-me' type='checkbox' value={rememberMe} onChange={(event) => setRememberMe(event.target.value)} />
                    </div>
                    <a href="./user" className="sign-in-button">Sign In</a>
                </form>
            </div>
        </section>
    );
};

export default login;
