import Icon from './circle-user-solid.svg';
import './style.css';
import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { setToken, loginUser, clearUser } from '../../feature/user';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [rememberMe, setRememberMe] = useState(false);

    const dispatch = useDispatch();
    const navigate = useNavigate();

    // Sélecteurs pour récupérer l'état de chargement et d'erreur depuis le store
    const { isLoading, error } = useSelector((state) => state.user);

    const resetForm = () => {
        setEmail('');
        setPassword('');
        setRememberMe(false);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            // Appel de l'action asynchrone loginUser (crée via createAsyncThunk)
            const resultAction = await dispatch(loginUser({ email, password, rememberMe }));

            // Vérifie si l'action est rejetée
            if (loginUser.fulfilled.match(resultAction)) {
                resetForm();
                navigate('/user');
            } else {
                // L'erreur est déjà gérée dans le store
                console.error(resultAction.error.message);
            }
        } catch (error) {
            console.error("Error during login:", error);
        }
    };

    return (
        <section className="main-login">
            <div className="sign-in-content">
                <img src={Icon} alt="user icon" className='usericon'/>
                <h1>Sign In</h1>
                <form onSubmit={handleSubmit}>
                    <div className="input-wrapper">
                        <label htmlFor="form-username">Username</label>
                        <input 
                            id='form-username' 
                            type='email' 
                            value={email} 
                            onChange={(event) => setEmail(event.target.value)} 
                            disabled={isLoading} 
                        />
                    </div>
                    <div className="input-wrapper">
                        <label htmlFor="password">Password</label>
                        <input 
                            id='password' 
                            type='password' 
                            value={password} 
                            onChange={(event) => setPassword(event.target.value)} 
                            disabled={isLoading} 
                        />
                    </div>
                    <div className="input-remember">                       
                        <input 
                            id='remember-me' 
                            type='checkbox' 
                            checked={rememberMe} 
                            onChange={(event) => setRememberMe(event.target.checked)} 
                            disabled={isLoading} 
                        />
                        <label htmlFor="remember-me">Remember me</label>
                    </div>
                    <button 
                        type="submit" 
                        className="sign-in-button" 
                        disabled={isLoading}>
                        {isLoading ? 'Signing In...' : 'Sign In'}
                    </button>
                    {error && <p className="error-message">Une erreur est survenue : {error}</p>}
                </form>
            </div>
        </section>
    );
};

export default Login;
