import { createSlice } from "@reduxjs/toolkit";

// Fonction pour récupérer le token depuis localStorage
const getTokenFromLocalStorage = () => {
    return localStorage.getItem('token') || null;
};

export const loginSlice = createSlice({
    name: "login",
    initialState: {
        isConnected: !!getTokenFromLocalStorage(), // Vérifie si un token est présent pour définir isConnected
        token: getTokenFromLocalStorage(), // Récupère le token depuis localStorage
        firstName: "",
        lastName: "",
        email: "",
        userName: "",
    },
    reducers: {
        setUserInfos: (state, action) => {
            state.firstName = action.payload.firstName;
            state.lastName = action.payload.lastName;
            state.email = action.payload.email;
            state.userName = action.payload.userName;
            state.isConnected = true;
        },
        setUserAuth: (state, action) => {
            state.email = action.payload.email;
            state.password = action.payload.password;
            state.token = action.payload.token;
            state.isConnected = true;
        },
        storeToken: (state, action) => {
            state.token = action.payload;
            state.isConnected = true; // Assure que isConnected est mis à jour

            localStorage.setItem('token', action.payload);
        },
        logout: (state) => {
            state.isConnected = false;
            state.email = "";
            state.password = "";
            state.firstName = "";
            state.lastName = "";
            state.token = null;

            localStorage.removeItem('token');
        },
    },
});

export const { setUserInfos, setUserAuth, storeToken, logout } = loginSlice.actions;
export default loginSlice.reducer;
